numbers = [10, 18, 11, 19, 20, 13, 17, 14, 12, 15]
a = numbers.__len__()
print(a)
print(len(numbers))

#  Using Predefined Methods
print(max(numbers))  # get the highest number in array
print(min(numbers))  # get the smallest number in array

temp = 0
for row in range(0,len(numbers)):
    for col in range(row+1,len(numbers)):
        if numbers[row] <= numbers[col]:
            temp = numbers[col]
            numbers[col] = numbers[row]
            numbers[row] = temp

